sp_widget.client_script:
function() {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectNote', function(event, data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(resp) {
			c.data.title = resp.data.note.title;
			c.data.note = resp.data.note.note;
			c.data.noteID = resp.data.note.sys_id;
		})
	})

	c.updateNote = function(updateType) {
		console.log("Updating note: " + c.data.title)

		$rootScope.$emit('updateNote', {
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		});

		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then(function(resp) {

		})
	}
}

sp_widget.script:
(function() {

	if (input && input.noteID) {
		gs.info("Hello")
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id");
			} else if (input.action == 'updateNote') {
				note.setValue('title', input.noteTitle);
				note.setValue('note', input.noteBody);
				var id = note.update();
				gs.info(id)
			}
		}
	}

})();
